  display: flex;
`;
const Key = styled.div`
  flex: 1;
  font-weight: 550;
`;

const CommentDetails = styled.div`
  font-weight: 550;
  display: inherit;
`;

const CommentValueDetail = styled.div`
  font-weight: normal;
  margin-left: 5px;
`;

const Value = styled.div`
  flex: 1;
  padding-left: 10px;
`;

const Hr = styled.hr`
  background-color: #b0b0b0;
  border: none;
